<script setup lang="ts">
import { ref, watchEffect } from 'vue'
import { useAuth } from '@/stores/auth'
import AuthLayoutVue from '@/layouts/AuthLayout.vue'

const valid = ref(true)
const authStore = useAuth()
const firstName = ref('')
const lastName = ref('')
const useremail = ref('')
const phoneNumber = ref('')
const password = ref('')
const confirmPassword = ref('')
const passwordErrorsMsg = ref<null | string>(authStore.error)
const timezone = ref(Intl.DateTimeFormat().resolvedOptions().timeZone)

watchEffect(() => {
  authStore.error
  passwordErrorsMsg.value = authStore.error
})
async function signUp() {
  passwordErrorsMsg.value = ''
  if (valid.value === null) {
    passwordErrorsMsg.value = 'Form is not valid'

    return
  }
  if (
    useremail.value === '' ||
    firstName.value === '' ||
    lastName.value === '' ||
    phoneNumber.value === '' ||
    password.value === '' ||
    confirmPassword.value === ''
  ) {
    passwordErrorsMsg.value = 'please write in  all fields'

    return
  }
  if (password.value !== confirmPassword.value) {
    passwordErrorsMsg.value = 'password and confirm password must be the same'

    return
  }

  const wasRegistered = await authStore.signUp({
    email: useremail.value,
    firstName: firstName.value.trim(),
    lastName: lastName.value.trim(),
    phoneNumber: phoneNumber.value,
    password: password.value,
    timezone: timezone.value
  })

  if (typeof wasRegistered !== 'string') {
    reset()

    useremail.value = ''
    firstName.value = ''
    lastName.value = ''
    phoneNumber.value = ''
    password.value = ''
    confirmPassword.value = ''

    return
  }
}

// form inputs models
const show1 = ref(false)

const form = ref<any>(null)

const selected = ref(null)
const rules = {
  required: (value) => !!value || 'Required.',
  min: (v) => v.length >= 8 || 'Min 8 characters',
  emailMatch: () => `The email and password you entered don't match`
}
const nameRules = ref([
  (v) => !!v || 'Name is required',
  (v) => (v && v.length <= 20) || 'Name must be less than 20 characters'
])
const emailRules = ref([
  (v) => !!v || 'E-mail is required',
  (v) => /.+@.+\..+/.test(v) || 'E-mail must be valid'
])

function validate() {
  form.value.validate()

  valid.value = true
}
function reset() {
  form.value.reset()
}
function resetValidation() {
  form.value.resetValidation()
  valid.value = true
}
</script>

